function write(sGA,iCommand)
{
    // Se nel sGA c'è un "-" (residuo EIBPC), prendo solo
    // ciò che c'è dopo
    if ( sGA.toString().indexOf("-")!==-1) {
        sGA=sGA.toString().split("-")[1]; 
    } 

    return({
          payload: (iCommand == 1 ? true : false),
            destination: sGA,
            dpt: "1.001"});
}

